import * as React from "react";
import * as CheckboxPrimitive from "@radix-ui/react-checkbox";
import clsx from "clsx";

export interface CheckboxProps
  extends React.ComponentPropsWithoutRef<typeof CheckboxPrimitive.Root> {}

export const Checkbox = React.memo(
  React.forwardRef<
    React.ElementRef<typeof CheckboxPrimitive.Root>,
    CheckboxProps
  >(function Checkbox({ className, ...props }, ref) {
    return (
      <CheckboxPrimitive.Root
        ref={ref}
        className={clsx(
          "peer h-4 w-4 shrink-0 rounded-[2px] border border-[color:var(--color-border-base)] ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-brand disabled:cursor-not-allowed disabled:opacity-60 disabled:border-[color:var(--foreground-secondary)] data-[state=checked]:bg-[color:var(--color-fg-base)] data-[state=checked]:text-white data-[state=checked]:border-[color:var(--color-fg-base)]",
          className
        )}
        {...props}
      >
        <CheckboxPrimitive.Indicator
          className={clsx("flex items-center justify-center text-[color:var(--background-primary)]")}
        >
          <svg
            width="12"
            height="12"
            viewBox="0 0 12 12"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M10 3L4.5 8.5L2 6"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </CheckboxPrimitive.Indicator>
      </CheckboxPrimitive.Root>
    );
  })
);
