import * as React from "react";
import {
  NavigationMenu,
  NavigationMenuList,
  NavigationMenuItem,
  NavigationMenuLink,
} from "@fragment_ui/ui";

export interface NavigationLink {
  label: string;
  href: string;
  external?: boolean;
  target?: string;
}

export interface DocumentationHeaderProps {
  logo?: React.ReactNode;
  links?: NavigationLink[];
  search?: React.ReactNode;
  actions?: React.ReactNode;
  mobileMenu?: React.ReactNode;
  blur?: boolean;
  height?: string;
  maxWidth?: string;
  mobileBreakpoint?: number;
  className?: string;
}

export function DocumentationHeader({
  logo,
  links = [],
  search,
  actions,
  mobileMenu,
  blur = true,
  height = "60px",
  maxWidth = "1536px",
  mobileBreakpoint = 1024,
  className,
}: DocumentationHeaderProps) {
  const [isMobile, setIsMobile] = React.useState(false);

  React.useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < mobileBreakpoint);
    };
    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  }, [mobileBreakpoint]);

  return (
    <header
      className={`fixed top-0 left-0 right-0 z-50 ${className || ""}`}
      style={{
        height,
        backgroundColor: blur
          ? "color-mix(in srgb, var(--background-primary) 60%, transparent)"
          : "var(--background-primary)",
        backdropFilter: blur ? "blur(12px)" : "none",
        WebkitBackdropFilter: blur ? "blur(12px)" : "none",
      }}
    >
      <div className="mx-auto" style={{ maxWidth }}>
        <div
          className="flex w-full items-center justify-between px-4 lg:px-8"
          style={{ height }}
        >
          <div className="flex items-center gap-4 sm:gap-8">
            {logo}
            {!isMobile && links.length > 0 && (
              <NavigationMenu className="ml-2 lg:ml-3">
                <NavigationMenuList className="gap-1">
                  {links.map((link, index) => {
                    const firstExternalIndex = links.findIndex((l) => l.external);
                    const showSeparator = firstExternalIndex > 0 && index === firstExternalIndex;
                    return (
                      <React.Fragment key={`${link.href}-${index}`}>
                        {showSeparator && (
                          <div
                            className="h-4 mx-2 border-l"
                            style={{
                              borderColor: "color-mix(in srgb, var(--foreground-primary) 10%, transparent)",
                            }}
                          />
                        )}
                        <NavigationMenuItem>
                          <NavigationMenuLink asChild className="py-1">
                            {link.external ? (
                              <a
                                href={link.href}
                                target={link.target || "_blank"}
                                rel={link.target === "_blank" ? "noopener noreferrer" : undefined}
                                className="rounded-md px-2 py-1"
                              >
                                {link.label}
                              </a>
                            ) : (
                              <a href={link.href} className="rounded-md px-2 py-1">
                                {link.label}
                              </a>
                            )}
                          </NavigationMenuLink>
                        </NavigationMenuItem>
                      </React.Fragment>
                    );
                  })}
                </NavigationMenuList>
              </NavigationMenu>
            )}
          </div>
          <div className="flex items-center flex-1 lg:flex-none justify-end lg:justify-start min-w-0">
            {search && (
              <div
                className={
                  isMobile
                    ? "flex-1 min-w-0 max-w-[calc(100%-120px)] ml-4 mr-2"
                    : "mr-4 flex-shrink-0 w-72 lg:w-80"
                }
              >
                {search}
              </div>
            )}
            {!isMobile && actions && (
              <>
                <div
                  className="h-4 border-l"
                  style={{
                    borderColor: "color-mix(in srgb, var(--foreground-primary) 10%, transparent)",
                  }}
                />
                <div style={{ marginLeft: "6px" }}>{actions}</div>
              </>
            )}
            {isMobile && (
              <>
                {actions && (
                  <div style={{ marginLeft: "6px" }} className="flex-shrink-0">
                    {actions}
                  </div>
                )}
                {mobileMenu && (
                  <div className="ml-2 flex-shrink-0">{mobileMenu}</div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}
