import * as React from "react";
import { TableOfContents, extractHeadings, type Heading } from "@fragment_ui/ui";

export interface DocumentationLayoutProps {
  children: React.ReactNode;
  header?: React.ReactNode;
  sidebar?: React.ReactNode;
  rightSidebar?: React.ReactNode;
  showTableOfContents?: boolean;
  contentSelector?: string;
  maxWidth?: string;
  contentTopPadding?: string;
  version?: string;
}

export function DocumentationLayout({
  children,
  header,
  sidebar,
  rightSidebar,
  showTableOfContents = true,
  contentSelector = "article",
  maxWidth = "1536px",
  contentTopPadding = "58px",
  version,
}: DocumentationLayoutProps) {
  const [headings, setHeadings] = React.useState<Heading[]>([]);

  React.useEffect(() => {
    const updateHeadings = () => {
      const extracted = extractHeadings(contentSelector);
      setHeadings(extracted);
    };
    updateHeadings();
    const timeoutId1 = setTimeout(updateHeadings, 100);
    const timeoutId2 = setTimeout(updateHeadings, 500);
    const observer = new MutationObserver(() => {
      updateHeadings();
    });
    const contentElement = document.querySelector(contentSelector);
    if (contentElement) {
      observer.observe(contentElement, {
        childList: true,
        subtree: true,
      });
    }
    return () => {
      clearTimeout(timeoutId1);
      clearTimeout(timeoutId2);
      observer.disconnect();
    };
  }, [children, contentSelector]);

  return (
    <div className="mx-auto" style={{ maxWidth }}>
      {header}
      <div className="flex" style={{ marginTop: header ? contentTopPadding : "0" }}>
        {sidebar && (
          <aside className="hidden lg:block flex-shrink-0">
            {sidebar}
          </aside>
        )}
        <main className="flex-1">
          <div
            className="px-3 lg:pl-8 lg:pr-[96px] pb-8"
            style={{
              paddingTop: header ? contentTopPadding : "0",
            }}
          >
            {children}
          </div>
        </main>
        {(showTableOfContents || rightSidebar) && (
          <aside className="hidden xl:block w-64 flex-shrink-0">
            {showTableOfContents && headings.length > 0 && (
              <div className="sticky" style={{ top: "74px", paddingTop: "44px" }}>
                <TableOfContents headings={headings} showActive={true} scrollOffset={100} />
              </div>
            )}
            {rightSidebar && (
              <div className="sticky" style={{ top: "74px", paddingTop: "44px" }}>
                {rightSidebar}
              </div>
            )}
          </aside>
        )}
      </div>
    </div>
  );
}
