import * as React from "react";
import clsx from "clsx";
import { Spinner } from "./spinner";

type Size = "sm" | "md" | "lg";

export interface InputProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, "size" | "children"> {
  size?: Size;
  loading?: boolean;
  error?: boolean;
  leadingIcon?: React.ReactNode;
  trailingIcon?: React.ReactNode;
}

const classesBy = {
  base: "flex items-center flex-1 rounded-[var(--Corner-Radius-2xs,6px)] bg-[color:var(--background-secondary,#18181B)] placeholder:text-[color:var(--foreground-secondary,#71717A)] focus:outline-none disabled:opacity-60",
  size: {
    sm: "py-[var(--spacing-xs,6px)] px-[var(--spacing-lg,12px)] gap-[var(--spacing-2xs,4px)]",
    md: "py-[var(--spacing-xs,6px)] px-[var(--spacing-lg,12px)] gap-[var(--spacing-2xs,4px)]",
    lg: "py-[var(--spacing-xs,6px)] px-[var(--spacing-lg,12px)] gap-[var(--spacing-2xs,4px)] text-base",
  },
  state: {
    default: "",
    error: "",
  },
} as const;

export const Input = React.memo(
  React.forwardRef<HTMLInputElement, InputProps>(
    function Input(
      {
        className,
        size = "md",
        loading,
        error,
        leadingIcon,
        trailingIcon,
        style,
        ...props
      },
      ref
    ) {
      return (
        <div className="relative flex items-center w-full">
          {leadingIcon && (
            <span className="absolute left-[var(--spacing-lg,12px)] flex items-center pointer-events-none z-10" style={{ width: "16px", height: "16px" }}>
              {leadingIcon}
            </span>
          )}
          <input
            ref={ref}
            className={clsx(
              classesBy.base,
              classesBy.size[size],
              error ? classesBy.state.error : classesBy.state.default,
              leadingIcon && "pl-[calc(var(--spacing-lg,12px)+16px+var(--spacing-2xs,4px))]",
              (trailingIcon || loading) && "pr-[calc(var(--spacing-lg,12px)+16px+var(--spacing-2xs,4px))]",
              className
            )}
            style={{
              fontFamily: "Geist, sans-serif",
              fontSize: "14px",
              fontStyle: "normal",
              fontWeight: 500,
              lineHeight: "160%",
              color: "var(--foreground-secondary)",
              ...style,
            }}
            {...props}
          />
          {loading && (
            <div className="absolute right-[var(--spacing-lg,12px)] top-1/2 -translate-y-1/2 pointer-events-none z-10" style={{ width: "16px", height: "16px" }}>
              <Spinner size="sm" />
            </div>
          )}
          {trailingIcon && !loading && (
            <span className="absolute right-[var(--spacing-lg,12px)] top-1/2 -translate-y-1/2 flex items-center pointer-events-none z-10" style={{ width: "16px", height: "16px" }}>
              {trailingIcon}
            </span>
          )}
        </div>
      );
    }
  )
);
