"use client";

import * as React from "react";
import { Card, CardContent } from "./card";
import clsx from "clsx";

export interface MetricCardProps {
  title: string;
  value: string | number;
  icon?: React.ReactNode;
  trend?: "up" | "down" | "neutral";
  trendValue?: string;
  description?: string;
  footer?: React.ReactNode;
  /**
   * Where to render the footer content.
   * @default "header"
   */
  footerPlacement?: "header" | "content";
  className?: string;
  onClick?: () => void;
}

export const MetricCard = React.memo(
  React.forwardRef<HTMLDivElement, MetricCardProps>(
    function MetricCard(
      {
        title,
        value,
        icon,
        trend,
        trendValue,
        description,
        footer,
        footerPlacement = "header",
        className,
        onClick,
      },
      ref
    ) {
      const getTrendIcon = (trend?: "up" | "down" | "neutral") => {
        switch (trend) {
          case "up":
            return "↑";
          case "down":
            return "↓";
          default:
            return "→";
        }
      };

      const getTrendColor = (trend?: "up" | "down" | "neutral") => {
        switch (trend) {
          case "up":
            return "text-[color:var(--color-status-success-base)]";
          case "down":
            return "text-[color:var(--color-status-error-base)]";
          default:
            return "text-[color:var(--color-fg-muted)]";
        }
      };

      return (
        <Card
          ref={ref}
          className={clsx(
            onClick && "cursor-pointer hover:shadow-lg transition-shadow",
            className
          )}
          onClick={onClick}
        >
          <div className="flex flex-row items-start justify-between gap-3 pb-2 w-full">
            <div className="flex items-center gap-2">
              {icon && (
                <div className="text-[color:var(--color-fg-muted)] flex-shrink-0">{icon}</div>
              )}
              <h3 className="text-sm font-normal text-[color:var(--color-fg-muted)] m-0">
                {title}
              </h3>
            </div>
            {footer && footerPlacement === "header" && <div className="flex-shrink-0">{footer}</div>}
          </div>
          <CardContent className="mt-1 mb-1">
            <div className="text-2xl font-semibold text-[color:var(--color-fg-base)]">
              {value}
            </div>
            {description && (
              <p
                className="text-xs text-[color:var(--color-fg-muted)] mt-1"
                style={{ color: "var(--color-fg-muted)" }}
              >
                {description}
              </p>
            )}
            {trend && trendValue && (
              <div
                className={clsx(
                  "text-xs flex items-center gap-1 mt-2",
                  getTrendColor(trend)
                )}
              >
                <span>{getTrendIcon(trend)}</span>
                <span>{trendValue}</span>
              </div>
            )}
            {footer && footerPlacement === "content" && (
              <div className="mt-4">{footer}</div>
            )}
          </CardContent>
        </Card>
      );
    }
  )
);

MetricCard.displayName = "MetricCard";
