"use client";

import { useState } from "react";
import { Button } from "@fragment_ui/ui";

interface NavigationHeaderProps {
  logo?: React.ReactNode;
  logoText?: string;
  logoHref?: string;
  links?: Array<{ href: string; label: string }>;
  actions?: React.ReactNode;
  mobileMenu?: boolean;
}

export function NavigationHeader({
  logo,
  logoText = "Fragment UI",
  logoHref = "/",
  links = [
    { href: "/components", label: "Components" },
    { href: "/blocks", label: "Blocks" },
    { href: "/docs/foundations/tokens", label: "Docs" },
  ],
  actions,
  mobileMenu = true,
}: NavigationHeaderProps) {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  return (
    <header className="border-b border-[color:var(--color-fg-muted)] bg-[color:var(--color-surface-1)] sticky top-0 z-50">
      <nav className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex items-center">
            {logoHref ? (
              <a
                href={logoHref}
                className="flex items-center space-x-2 text-xl font-semibold"
              >
                {logo}
                {logoText && <span>{logoText}</span>}
              </a>
            ) : (
              <div className="flex items-center space-x-2 text-xl font-semibold">
                {logo}
                {logoText && <span>{logoText}</span>}
              </div>
            )}
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-6">
            {links.map((link) => (
              <a
                key={link.href}
                href={link.href}
                className="text-sm hover:text-[color:var(--color-brand-primary)] transition-colors"
              >
                {link.label}
              </a>
            ))}
          </div>

          {/* Actions */}
          <div className="hidden md:flex items-center space-x-4">
            {actions || (
              <>
                <Button variant="ghost" size="sm">
                  Sign In
                </Button>
                <Button variant="solid" size="sm">
                  Get Started
                </Button>
              </>
            )}
          </div>

          {/* Mobile Menu Button */}
          {mobileMenu && (
            <div className="md:hidden">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              >
                {mobileMenuOpen ? "✕" : "☰"}
              </Button>
            </div>
          )}
        </div>

        {/* Mobile Menu */}
        {mobileMenu && mobileMenuOpen && (
          <div className="md:hidden border-t border-[color:var(--color-fg-muted)] py-4">
            <div className="flex flex-col space-y-4">
              {links.map((link) => (
                <a
                  key={link.href}
                  href={link.href}
                  className="text-sm hover:text-[color:var(--color-brand-primary)] transition-colors"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  {link.label}
                </a>
              ))}
              {actions && <div className="pt-4">{actions}</div>}
            </div>
          </div>
        )}
      </nav>
    </header>
  );
}
