"use client";

import * as React from "react";
import * as NavigationMenuPrimitive from "@radix-ui/react-navigation-menu";
import clsx from "clsx";

export interface NavigationMenuProps
  extends React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Root> {
  /**
   * Variant of the navigation menu
   * @default "default"
   * - "default": Standard navigation menu
   * - "header": Header variant with backdrop blur and fixed positioning
   */
  variant?: "default" | "header";
  /**
   * Enable backdrop blur effect (only applies when variant="header")
   * @default false
   */
  blur?: boolean;
  /**
   * Height of the header (only applies when variant="header")
   * @default "60px"
   */
  height?: string;
  /**
   * Maximum width of the container (only applies when variant="header")
   * @default "1536px"
   */
  maxWidth?: string;
}

const NavigationMenu = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Root>,
  NavigationMenuProps
>(({ className, children, variant = "default", blur = false, height = "60px", maxWidth = "1536px", ...props }, ref) => {
  // Header variant with backdrop blur and fixed positioning
  if (variant === "header") {
    return (
      <header
        className={clsx("fixed top-0 left-0 right-0 z-50", className)}
        style={{
          height,
          backgroundColor: blur
            ? "color-mix(in srgb, var(--background-primary) 60%, transparent)"
            : "var(--background-primary)",
          backdropFilter: blur ? "blur(12px)" : "none",
          WebkitBackdropFilter: blur ? "blur(12px)" : "none",
        }}
      >
        <div className="mx-auto" style={{ maxWidth }}>
          <NavigationMenuPrimitive.Root
            ref={ref}
            className={clsx(
              "relative z-10 flex max-w-max flex-1 items-center justify-center",
              className
            )}
            {...props}
          >
            {children}
            <NavigationMenuViewport />
          </NavigationMenuPrimitive.Root>
        </div>
      </header>
    );
  }

  // Default variant
  return (
    <NavigationMenuPrimitive.Root
      ref={ref}
      className={clsx(
        "relative z-10 flex max-w-max flex-1 items-center justify-center",
        className
      )}
      {...props}
    >
      {children}
      <NavigationMenuViewport />
    </NavigationMenuPrimitive.Root>
  );
});
NavigationMenu.displayName = NavigationMenuPrimitive.Root.displayName;

const NavigationMenuList = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.List>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.List>
>(({ className, ...props }, ref) => (
  <NavigationMenuPrimitive.List
    ref={ref}
    className={clsx(
      "group flex flex-1 list-none items-center justify-center space-x-1",
      className
    )}
    {...props}
  />
));
NavigationMenuList.displayName = NavigationMenuPrimitive.List.displayName;

const NavigationMenuItem = NavigationMenuPrimitive.Item;

const NavigationMenuTrigger = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Trigger>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Trigger>
>(({ className, children, style, ...props }, ref) => (
  <NavigationMenuPrimitive.Trigger
    ref={ref}
    className={clsx(
      "group inline-flex h-10 w-max items-center justify-center rounded-[var(--radius-md)] bg-transparent px-4 py-2 text-sm font-normal transition-colors hover:bg-[color:var(--color-surface-2)] focus:bg-[color:var(--color-surface-2)] focus:outline-none disabled:pointer-events-none disabled:opacity-50 data-[active]:bg-[color:var(--color-surface-2)] data-[state=open]:bg-[color:var(--color-surface-2)] text-[color:var(--color-fg-base)] no-underline",
      className
    )}
    style={{
      textDecoration: "none",
      ...style,
    }}
    {...props}
  >
    {children}
    <span className="ml-1">â–¼</span>
  </NavigationMenuPrimitive.Trigger>
));
NavigationMenuTrigger.displayName = NavigationMenuPrimitive.Trigger.displayName;

const NavigationMenuContent = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Content>
>(({ className, ...props }, ref) => (
  <NavigationMenuPrimitive.Content
    ref={ref}
    className={clsx(
      "left-0 top-0 w-full data-[motion^=from-]:animate-in data-[motion^=to-]:animate-out data-[motion^=from-]:fade-in data-[motion^=to-]:fade-out data-[motion=from-end]:slide-in-from-right-52 data-[motion=from-start]:slide-in-from-left-52 data-[motion=to-end]:slide-out-to-right-52 data-[motion=to-start]:slide-out-to-left-52 md:absolute md:w-auto",
      className
    )}
    {...props}
  />
));
NavigationMenuContent.displayName = NavigationMenuPrimitive.Content.displayName;

const NavigationMenuLink = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Link>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Link>
>(({ className, style, ...props }, ref) => (
  <NavigationMenuPrimitive.Link
    ref={ref}
    className={clsx(
      "group inline-flex h-auto w-max items-center justify-center rounded-md bg-transparent px-2.5 py-1.5 text-sm font-normal transition-colors hover:bg-[color:var(--color-surface-2)] focus:bg-[color:var(--color-surface-2)] focus:outline-none disabled:pointer-events-none disabled:opacity-50 text-[color:var(--color-fg-base)] no-underline",
      className
    )}
    style={{
      textDecoration: "none",
      ...style,
    }}
    {...props}
  />
));
NavigationMenuLink.displayName = NavigationMenuPrimitive.Link.displayName;

const NavigationMenuViewport = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Viewport>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Viewport>
>(({ className, ...props }, ref) => (
  <div className={clsx("absolute left-0 top-full flex justify-center")}>
    <NavigationMenuPrimitive.Viewport
      className={clsx(
        "origin-top-center relative mt-1.5 h-[var(--radix-navigation-menu-viewport-height)] w-full overflow-hidden rounded-[var(--radius-md)] border border-[color:var(--color-fg-muted)] bg-[color:var(--color-surface-1)] text-[color:var(--color-fg-base)] shadow-lg data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-90 md:w-[var(--radix-navigation-menu-viewport-width)]",
        className
      )}
      ref={ref}
      {...props}
    />
  </div>
));
NavigationMenuViewport.displayName =
  NavigationMenuPrimitive.Viewport.displayName;

const NavigationMenuIndicator = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Indicator>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Indicator>
>(({ className, ...props }, ref) => (
  <NavigationMenuPrimitive.Indicator
    ref={ref}
    className={clsx(
      "top-full z-[1] flex h-1.5 items-end justify-center overflow-hidden data-[state=visible]:animate-in data-[state=hidden]:animate-out data-[state=hidden]:fade-out data-[state=visible]:fade-in",
      className
    )}
    {...props}
  >
    <div className="relative top-[60%] h-2 w-2 rotate-45 rounded-tl-sm bg-[color:var(--color-surface-1)] shadow-md" />
  </NavigationMenuPrimitive.Indicator>
));
NavigationMenuIndicator.displayName =
  NavigationMenuPrimitive.Indicator.displayName;

export {
  NavigationMenu,
  NavigationMenuList,
  NavigationMenuItem,
  NavigationMenuContent,
  NavigationMenuTrigger,
  NavigationMenuLink,
  NavigationMenuIndicator,
  NavigationMenuViewport,
};
